mapper.select:
 	select exno,exid, extitle, exImage,exTermStart,exTermEnd,exhits from
 	(select rownum rno,exid, extitle, exImage, to_char(extermstart,'yyyy-mm-dd')extermstart, to_char(extermend,'yyyy-mm-dd')extermend,exhits from
(select exno, exid, extitle, exImage,exTermStart,exTermEnd,exhits from offnow order by exTermStart desc))where rno between #{start} and #{end}

choose.when:
update offnow set exTitle=#{exTitle}, exImage=#{exImage}
,exCategory=#{exCategory},exContent=#{exContent}
 where exid=#{exId}

choose.otherwise:
 update offNow set exTitle=#{exTitle}
,exCategory=#{exCategory},exContent=#{exContent}
 where exid=#{exId}
